import UIKit

protocol AuthRoutingLogic: CommonRoutingLogic  {
    func routToMain()
    func routToResetPass()
}

protocol AuthDataPassing {
    var dataStore: AuthDataStore? { get }
}

class AuthRouter: NSObject, AuthRoutingLogic, AuthDataPassing {
    weak var viewController: AuthViewController?
    var dataStore: AuthDataStore?
    var modalControllersQueue = Queue<UIViewController>()

    // MARK: Routing

    func routToMain(){
        viewController?.masterRouter?.pushChild(viewControllerClass: MainViewController.self, storyboard: .main)
    }

    func routToResetPass(){
        let vc = ResetPassViewController.instantiate(fromStoryboard: .autorization)
        viewController?.masterRouter?.presentNextChild(viewController: vc)
    }

}
