import UIKit

protocol AchievesBusinessLogic {
    func fetchAchievements()
}

protocol AchievesDataStore {
    var achievements: [AchievementsResponse] { get set }

}

class AchievesInteractor: AchievesBusinessLogic, AchievesDataStore {
    
    var achievements: [AchievementsResponse] = []
    
    var presenter: AchievesPresentationLogic?
    let service: ProfileProtocol = ProfileService()

    func fetchAchievements(){
        service.getAchievements { [weak self]  (result) in
            switch result {
            case .success(let response):
                self?.presenter?.presentAchieves(response)
            case .failure(let error):
                self?.presenter?.presentError(error: error)
            }
        }
    }
}
