import UIKit
import ObjectMapper

enum AnimalsGame {
    class Animal: Hashable  {
        var name: String = ""
        var image: UIImage?
        var sound: URL

        init(name: String, image: UIImage, sound: URL) {
            self.name = name
            self.image = image
            self.sound = sound
        }

        required init?(local: AnimalLocal) {
            if
                let localImage = UIImage(contentsOfFile: local.image),
                let soundUrl = URL(string: local.sound)
            {
                name = local.name
                sound = soundUrl
                image = localImage
            } else {
                return nil
            }
        }

        func hash(into hasher: inout Hasher) {
            hasher.combine(name)
        }
        static func == (lhs: AnimalsGame.Animal, rhs: AnimalsGame.Animal) -> Bool {
            return lhs.name == rhs.name
        }
    }
    
    final class AnimalLocal: Mappable {
        var name: String = ""
        var image: String = ""
        var sound: String = ""

        init() { }
        required init?(map: Map) { }

        func mapping(map: Map) {
            name <- map["name"]
            image <- map["image"]
            sound <- map["sound"]
        }
    }

    struct PackAnimal {
        let name: String
        var image: String
        var sound: String
        var id: Int
    }

    enum Complexity {
        case easy
        case hard
    }

