import UIKit

enum ColorsMaster {

    enum Colors: String {
        case white = "Белый"
        case black = "Черный"

        case red = "Красный"
        case orange = "Оранжевый"
        case yellow = "Желтый"
        case green = "Зеленый"
        case lightBlue = "Голубой"
        case blue = "Синий"
        case violet = "Фиолетовый"

        var sound: String {
            switch self {
                case .white:
                    return ""
                case .black:
                    return ""
                case .red:
                    return ""
                case .orange:
                    return ""
                case .yellow:
                    return ""
                case .green:
                    return ""
                case .lightBlue:
                    return ""
                case .blue:
                    return ""
                case .violet:
                    return ""
            }
        }
    }

    static let DefaultColors: [Colors] = [
        Colors.yellow,
        Colors.green,
        Colors.white
    ]

    static let Pack: [Colors: [String]] = [
        Colors.white: ["", ""],
        Colors.black: ["", ""],
        Colors.red: ["", ""],
        Colors.orange: ["", ""],
        Colors.yellow: ["", ""],
        Colors.green: ["", ""],
        Colors.lightBlue: ["", ""],
        Colors.blue: ["", ""],
        Colors.violet: ["", ""]
    ]
}

extension ColorsMaster.Colors {
    var value: [UIColor] {
        get {
            switch self {
                case .yellow:
                    return [UIColor.ColorsGame.yellowOne, UIColor.ColorsGame.yellowTwo]
                case .orange:
                    return [UIColor.ColorsGame.orangeOne, UIColor.ColorsGame.orangeTwo]
                case .blue:
                    return [UIColor.ColorsGame.blueOne, UIColor.ColorsGame.blueTwo]
                case .red:
                    return [UIColor.ColorsGame.redOne, UIColor.ColorsGame.redTwo]
                case .green:
                    return [UIColor.ColorsGame.greenOne, UIColor.ColorsGame.greenTwo]
                case .white:
                    return [.white, .white]
                case .black:
                    return [.black, .black]
                case .lightBlue:
                    return [UIColor.ColorsGame.lightBlueOne, UIColor.ColorsGame.lightBlueTwo]
                case .violet:
                    return [UIColor.ColorsGame.violetOne, UIColor.ColorsGame.violetTwo]
            }
        }
    }
}
