import UIKit

protocol OfflinePlaylistBusinessLogic {
    func getPlaylist()
}

protocol OfflinePlaylistDataStore {
    
}

class OfflinePlaylistInteractor: OfflinePlaylistBusinessLogic, OfflinePlaylistDataStore {
    var presenter: OfflinePlaylistPresentationLogic?
    var albumId: Int?
    let localMusicService = MusicLoaderService()
    let service: PlaylistServiceProtocol = MainPlaylistService()

    
    func getPlaylist() {
        let tracks = localMusicService.getList()
        presenter?.presentLocal(tracks: tracks) { [weak self] ids in
            self?.sync(ids)
        }
    }

    func sync(_ ids: [Int]) {
        service.syncDownload(ids: ids) { _ in

        }
    }
}
